export declare const useStateMutationActionString = "export const useStateMutationAction = (initialState) => {\n  const [state, setState] = React.useState(initialState);\n\n  const setNewState = React.useCallback(\n    (newState) => {\n      const prevState = state;\n\n      Hub.dispatch(\n        UI_CHANNEL,\n        {\n          event: ACTION_STATE_MUTATION_STARTED,\n          data: { prevState, newState },\n        },\n        EVENT_ACTION_CORE_STATE_MUTATION,\n        AMPLIFY_SYMBOL,\n      );\n\n      setState(newState);\n\n      Hub.dispatch(\n        UI_CHANNEL,\n        {\n          event: ACTION_STATE_MUTATION_FINISHED,\n          data: { prevState, newState },\n        },\n        EVENT_ACTION_CORE_STATE_MUTATION,\n        AMPLIFY_SYMBOL,\n      );\n    },\n    [state],\n  );\n\n  return [state, setNewState];\n};";
